document.addEventListener('DOMContentLoaded', function() {
	let gallery = document.querySelector('.gallery');
	let pureLoading = document.querySelector('.pure-loading');
	setTimeout(function() {
		pureLoading.style.display = 'block';
	}, 500);
	setTimeout(function() {
		gallery.style.display = 'flex';
		pureLoading.remove();
	}, 3500);
});
